    def run_machine_code(self):
        sys.stdout=open(r"console.txt","w")
        machine_code=self.plainTextEdit.toPlainText()
        with open(r'code.mc',"w") as f:
            f.write(machine_code)
            #phase2_final.choice=2
        #print(self.radioButton.isChecked(),self.radioButton_3.isChecked(),"asdddddskfjkajsdfiijlakdsfasdfkl")
        if(self.radioButton.isChecked()):
            phase2_final.Run_program(2)
            #choice=1
            #os.system('phase2_final.py')
        elif(self.radioButton_3.isChecked()):
            #choice=2
            phase2_final.Run_program(1)
            #os.system('phase2_final.py')
        else:
            #choice=3
            #os.system('phase2_final.py')
            phase2_final.Run_program(3)
        #execfile('phase2_final.py')
        self.plainTextEdit_2.clear()
        self.plainTextEdit_2.setReadOnly(True)
        console=open(r'console.txt').read()
        
        self.plainTextEdit_2.setPlainText(console)
        f=open('register.mc','r')
        register_values=json.load(f)
        f.close()
        #print(register_values)
        for i in range(32):
            a=bin(i)
            a=a[2:].zfill(5)
            num=QtWidgets.QTableWidgetItem(str(register_values[a]))
            b=hex(register_values[a])
            b='0x'+b[2:].zfill(8)
            num1=QtWidgets.QTableWidgetItem(b.upper())
            #self.tableWidget.setCurrentCell(2,2)
            self.tableWidget.setItem(i,0,num1)
            self.tableWidget.setItem(i,1,num)
        f=open('memory.mc','r')
        memory_values=json.load(f)
        f.close()
        self.tableWidget_2.setRowCount(len(memory_values))
        cont=0
        for i,j in memory_values.items():
            num=QtWidgets.QTableWidgetItem(str(i))
            num1=QtWidgets.QTableWidgetItem(str(j[:2]))
            num2=QtWidgets.QTableWidgetItem(str(j[2:4]))
            num3=QtWidgets.QTableWidgetItem(str(j[4:6]))
            num4=QtWidgets.QTableWidgetItem(str(j[6:]))
            #b=hex(register_values[a])
            #b='0x'+b[2:].zfill(8)
            #num1=QtWidgets.QTableWidgetItem(b.upper())
            #self.tableWidget.setCurrentCell(2,2)
            self.tableWidget_2.setItem(cont,0,num)
            self.tableWidget_2.setItem(cont,1,num1)
            self.tableWidget_2.setItem(cont,2,num2)
            self.tableWidget_2.setItem(cont,3,num3)
            self.tableWidget_2.setItem(cont,4,num4)
            cont=cont+1
            #self.tableWidget.setItem(i,1,num)
        
    def reset_pushed(self):
        self.plainTextEdit.clear()
        self.plainTextEdit_2.clear()      
    def exit_pushed(self):
        QtCore.QCoreApplication.instance().quit()
